def test_error_scenarios():
    """Test various error scenarios for the chat system"""
    print("\nâš ï¸  Testing Error Scenarios...")
    print("=" * 40)
    
    # Test unauthenticated access
    print("\nğŸ”’ Testing unauthenticated access...")
    response = requests.get(f'{BASE_URL}/chats/')
    if response.status_code == 401:
        print("âœ… Correctly blocked unauthenticated access to chats")
    else:
        print(f"âŒ Unexpected response for unauthenticated access: {response.status_code}")
    
    # Test invalid chat ID
    print("\nğŸ” Testing invalid chat ID...")
    # Create a dummy authenticated user for this test
    user_data = {
        "username": "error_test_user",
        "email": "error@example.com",
        "password": "testpass123",
        "display_name": "Error Test User"
    }
    
    response = requests.post(f'{BASE_URL}/accounts/', json=user_data)
    if response.status_code == 201:
        # Authenticate
        auth_data = {
            "action": "login",
            "username": "error_test_user",
            "password": "testpass123"
        }
        
        response = requests.post(f'{BASE_URL}/auth/', json=auth_data)
        if response.status_code == 200:
            token = response.json().get('token')
            headers = {'Authorization': f'Bearer {token}'}
            
            # Test invalid chat ID
            response = requests.get(f'{BASE_URL}/chats/invalid_chat_id/', headers=headers)
            if response.status_code == 404:
                print("âœ… Correctly returned 404 for invalid chat ID")
            else:
                print(f"âŒ Unexpected response for invalid chat ID: {response.status_code}")
            
            # Test sending message to invalid chat
            message_data = {"text": "This should fail"}
            response = requests.post(f'{BASE_URL}/chats/invalid_chat_id/messages/', 
                                   json=message_data, headers=headers)
            if response.status_code == 404:
                print("âœ… Correctly blocked message to invalid chat")
            else:
                print(f"âŒ Unexpected response for message to invalid chat: {response.status_code}")
    
    print("âœ… Error scenario testing completed!")

